import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import * as React from 'react';

import TabBarIcon from '../components/TabBarIcon';
import HomeScreen from '../screens/HomeScreen';
import LinksScreen from '../screens/LinksScreen';
import ExploreScreen from '../screens/ExploreScreen';
import AddPost from '../screens/AddPost';
import SettingsScreen from '../screens/SettingsScreen';
import UserScreen from '../screens/UserScreen';
import Colors from '../constants/Colors.js';
import Fonts from '../constants/Fonts.js';
import { useSelector } from 'react-redux';

const BottomTab = createBottomTabNavigator();
const INITIAL_ROUTE_NAME = 'Home';

export default function BottomTabNavigator({ navigation, route }) {
  const user = useSelector(state => state.auth.user)
  navigation.setOptions({ headerTitle: getHeaderTitle(route, user), headerStyle: {backgroundColor: '#0f0f0f'}, 
  headerTitleStyle: { color: Colors.content, fontFamily: Fonts.logo, fontSize: 25 }, 
  headerTintColor: Colors.content, headerLeft: null});
  return (
    <BottomTab.Navigator initialRouteName={INITIAL_ROUTE_NAME} tabBarOptions={{showLabel: false, style:{backgroundColor: '#0f0f0f', borderTopColor: '#0f0f0f'}}} >
      <BottomTab.Screen
        name="Home"
        component={HomeScreen}
        options={{
          tabBarIcon: ({ focused }) => <TabBarIcon focused={focused} name="ios-home" />,
        }}
      />
      <BottomTab.Screen
        name="Explore"
        component={ExploreScreen}
        options={{
          tabBarIcon: ({ focused }) => <TabBarIcon focused={focused} name="ios-search" />,
        }}
      />
      <BottomTab.Screen
        name="AddPost"
        component={AddPost}
        options={{
          tabBarIcon: ({ focused }) => <TabBarIcon focused={focused} name="ios-add" />,
        }}
      />
      <BottomTab.Screen
        name="User"
        component={UserScreen}
        options={{
          tabBarIcon: ({ focused }) => <TabBarIcon focused={focused} name="md-person" />,
        }}
      />
      <BottomTab.Screen
        name="Settings"
        component={SettingsScreen}
        options={{
          tabBarIcon: ({ focused }) => <TabBarIcon focused={focused} name="ios-settings" />,
        }}
      />
    </BottomTab.Navigator>
  );
}

function getHeaderTitle(route, username) {
  const routeName = route.state?.routes[route.state.index]?.name ?? INITIAL_ROUTE_NAME;

  switch (routeName) {
    case 'Home':
      return 'CM';
    case 'Explore':
      return 'EXPLORE';
    case 'AddPost':
      return 'NEW POST';
    case 'User':
      return username ? username.toUpperCase().split(' ').map(i => i.charAt(0)) : "Hi";
    case 'Settings':
      return 'OPTIONS';
  }
}
